# tests/serializers.pyfrom rest_framework import serializersfrom .models import Test, Question, Answerclass AnswerSerializer(serializers.ModelSerializer):    class Meta:        model = Answer        fields = ['id', 'text']class QuestionSerializer(serializers.ModelSerializer):    answers = AnswerSerializer(many=True, read_only=True)    class Meta:        model = Question        fields = ['id', 'text', 'points', 'answers']class TestSerializer(serializers.ModelSerializer):    questions = QuestionSerializer(many=True, read_only=True)    class Meta:        model = Test        fields = ['id', 'test_id', 'name', 'school', 'class_name', 'start_date', 'duration', 'questions']