from rest_framework import serializersfrom .models import School, ClassGroup, Student, Test, Question, AnswerOption, StudentAnswerclass SchoolSerializer(serializers.ModelSerializer):    class Meta:        model = School        fields = '__all__'class ClassGroupSerializer(serializers.ModelSerializer):    class Meta:        model = ClassGroup        fields = '__all__'class StudentSerializer(serializers.ModelSerializer):    class Meta:        model = Student        fields = '__all__'class TestSerializer(serializers.ModelSerializer):    class Meta:        model = Test        fields = '__all__'class QuestionSerializer(serializers.ModelSerializer):    class Meta:        model = Question        fields = '__all__'class AnswerOptionSerializer(serializers.ModelSerializer):    class Meta:        model = AnswerOption        fields = '__all__'class StudentAnswerSerializer(serializers.ModelSerializer):    class Meta:        model = StudentAnswer        fields = '__all__'    def create(self, validated_data):        answer = StudentAnswer.objects.create(**validated_data)        answer.check_answer()        return answer