from django.urls import path, includefrom rest_framework.routers import DefaultRouterfrom .views import *router = DefaultRouter()router.register(r'schools', SchoolViewSet)router.register(r'classgroups', ClassGroupViewSet)router.register(r'students', StudentViewSet)router.register(r'tests', TestViewSet)router.register(r'questions', QuestionViewSet)router.register(r'answeroptions', AnswerOptionViewSet)router.register(r'studentanswers', StudentAnswerViewSet)urlpatterns = [    path('api/', include(router.urls)),    path('', home)]