from django.urls import path, includefrom rest_framework.routers import DefaultRouterfrom testsystem.views import *router = DefaultRouter()router.register(r'schools', SchoolViewSet)router.register(r'classgroups', ClassGroupViewSet)router.register(r'students', StudentViewSet)router.register(r'tests', TestViewSet)router.register(r'questions', QuestionViewSet)router.register(r'answers', AnswerViewSet)urlpatterns = [    path('api/', include(router.urls)),    path('', home)]